var chai = require('chai');
var bloom = require('../packages/web3-utils/src/bloomFilter.js');
var assert = chai.assert;

var addressTests = [
	{bloom: '', address: '0xa0c0ffee11111111111111111111111111111111111111111111111111111111', result: true},
	{bloom: '', address: '0xa0c0ffee99999999999999999999999999999999999999999999999999999999', result: false},
];

describe('web3-utils/bloomFilter', function() {
	it('testAddress', function() {
		addressTests.forEach(function(test) {
			assert.equal(bloom.testAddress(test.bloom, test.address), test.result);
		});
	});
});
